function restoreToggleStates(){document.querySelectorAll('input[type="checkbox"][id^="webhook-toggle-"]').forEach(e=>{let t=localStorage.getItem(e.id);null!==t&&(e.checked=JSON.parse(t))})}function saveToggleState(e){e.target.matches('input[type="checkbox"][id^="webhook-toggle-"]')&&localStorage.setItem(e.target.id,e.target.checked)}document.addEventListener("DOMContentLoaded",restoreToggleStates),document.addEventListener("change",saveToggleState);
